sp_widget.client_script:
function($scope,$rootScope) {
		/* widget controller */
		var c = this;

		$rootScope.$on('selectNote', function(event,data) { 
			console.log("note id client " + data);
			c.server.get({
				action : 'getNote',
				noteID : data
			})
			.then(function(r){
				c.data.title = r.data.note.title;
				c.data.note = r.data.note.note;
				c.data.noteID = r.data.note.sys_id;
			});
		});

	 c.updateRecord = function(actionName) {
		 console.log("Trying to update record ");
		 c.server.get({
			 action : "updateRecord",
			 noteID : c.data.noteID,
			 noteBody : c.data.note,
			 noteTitle : c.data.title
		 })
		 .then(function(r){
			 var d = {};
			 d.note = r.data.note.note;
			 d.title = r.data.note.title;
			 d.id = r.data.note.sys_id;
			 console.log("Update has been done to record");
			 console.log("Record is updated with values " + JSON.stringify(d));
			 $rootScope.$emit("updateRecord", d);
		 });
	 };
	}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	if (input && input.noteID) {
		console.log("Action name is " + input.action);
		var note = new GlideRecord('x_snc_createnotes_note');

			if (note.get(input.noteID)) {
				if (input.action == 'getNote') {
					console.log("if action name is " + input.action);
					data.note = {};
					$sp.getRecordValues(data.note, note, "title, note, sys_id");
				}
				else if (input.action == "updateRecord") {
					note.note = input.noteBody;
					note.title = input.noteTitle;
					note.update();
					data.note = {};
					$sp.getRecordValues(data.note, note, "title, note, sys_id");
				}
			}

	}


})();
