sp_widget.client_script:
function($rootScope, $scope) {
  /* widget controller */
  var c = this;

	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);

		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	};

	$rootScope.$on('updateRecord' , function(event, data){
		console.log("In List widget recevied the data " + JSON.stringify(data));	

		c.server.get({
			action : "getUpdatedData",
			noteID : data.id,
			note: data.note,
			title: data.title
		})
			.then(function(r){
			console.log("update signal is received from body widget " + JSON.stringify(r.data));
			c.data.notes = r.data.notes;
		});
	});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while (noteGR.next()) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		noteObj.note = noteGR.getValue('note').slice(0, 20);

		data.notes.push(noteObj);
	}

	if (input && input.action == "getUpdatedData") {
		console.log("Server side action name " + input.action);
		for ( var i = 0 ; i < data.notes.length; i++) {
			if (data.notes[i].sys_id == input.noteID) {
				data.notes[i].note = input.note;
				data.notes[i].title = input.title;
			}
		}
	}

})();
